mapper.select:
SELECT *
FROM workout_campaigns
WHERE family_member_id = #{familyMemberId}
  AND DATE(start_at) = DATE(#{startAt})
  AND DATE(end_at) = DATE(#{endAt});

mapper.update:
UPDATE workout_campaigns
SET calorie_goal   = #{calorieGoal},
    calorie_burned = #{calorieBurned},
    start_at       = #{startAt},
    end_at         = #{endAt}
WHERE id = #{id}
